const pg = require('pg');
const config = {
  user: 'your_username',
  database: 'your_database_name',
  password: 'your_password',
  host: 'hostname',
  port: 5432,
  max: 10,
  idleTimeoutMillis: 30000
};

const pool = new pg.Pool(config);

pool.on('connect', () => {
  console.log('connected to the database');
});

const query = (text, params) => pool.query(text, params);

module.exports = {
  query
};
